parameters.jsCode:
inputText=$input.first().json.message.text;
// Remove "generate video" prefix (case-insensitive) and trim whitespace
const cleaned = inputText.replace(/^generate video/i, '').trim();

// Split at the first comma only
const [videoPrompt, captionIdea] = cleaned.split(/,(.+)/).map(s => s.trim());

// Return as a JSON object
return {
  videoPrompt,
  captionIdea
};

