parameters.jsCode:
const message = $input.first().json.message.text;
const chatId = $input.first().json.message.chat.id;

// Split by space to get URL and number
const parts = message.trim().split(/\s+/);

// Find the URL part
const urlPart = parts.find(part => part.includes('youtube.com') || part.includes('youtu.be'));

// Find the number part
const numberPart = parts.find(part => /^\d+$/.test(part));

if (urlPart && numberPart) {
	const number = parseInt(numberPart);

	// Validate number range
	if (number >= 1 && number <= 10) {
		return [{
			json: {
				action: 'process',
				'YouTube URL': urlPart,
				'How many shorts to generate?': number,
				chat_id: chatId
			}
		}];
	} else {
		return [{
			json: {
				action: 'invalid_number',
				chat_id: chatId,
				message: 'Please use a number between 1 and 10'
			}
		}];
	}
} else {
	return [{
		json: {
			action: 'invalid_format',
			chat_id: chatId,
			message: `Send: YouTube_URL Number\nExample:\nhttps://youtu.be/abc123 5`
		}
	}];
}

const allVideos = $input.all();

const totalExpected = allVideos.length;
const readyCount = allVideos.filter(v => v.json.status === 'ready').length;

// Si tous les items sont présents et tous sont 'ready', alors true
const allReady = totalExpected > 0 && readyCount === totalExpected;

return allVideos.map(video => ({
  json: {
    ...video.json,
    is_everything_ready: allReady
  }
}));

let postsPerDay;
let startHour;
let endHour;
for (const item of items) {
const setting = item.json["Publishing Settings"];
const value = item.json["col_2"];
if (setting === "Post per Day") {
postsPerDay = parseInt(value, 10);
} else if (setting === "Start Time") {
startHour = parseInt(value.split(":")[0], 10);
} else if (setting === "End Time") {
endHour = parseInt(value.split(":")[0], 10);
}
}
return [
{
json: {
postsPerDay,
startHour,
endHour
}
}
];

// Get existing scheduled posts
const existingPosts = $input.all();
// Safe fallback: if the sheet is empty, use an empty object
const scheduleSettings = ($('Fetch Already Scheduled Shorts').first() || {
json: {} }).json;
// Find the latest scheduled time
let lastScheduledTime = new Date();
if (existingPosts.length > 0) {
// Get all scheduled times and find the latest
const scheduledTimes = existingPosts
.map(post => new Date(post.json['Schedule Time']))
.filter(date => !isNaN(date))
.sort((a, b) => b - a); // Sort descending
if (scheduledTimes.length > 0) {
lastScheduledTime = scheduledTimes[0];
}
}
// Pass this to the next node
return [{
json: {
lastScheduledTime: lastScheduledTime.toISOString(),
postsPerDay: scheduleSettings.postsPerDay,
startHour: scheduleSettings.startHour,
endHour: scheduleSettings.endHour
}
}];

const postsPerDay = parseInt($('Extract Scheduling Parameters (Posts/Day, Hours)').first().json.postsPerDay);
const startHour = parseInt($('Extract Scheduling Parameters (Posts/Day, Hours)').first().json.startHour);
const endHour = parseInt($('Extract Scheduling Parameters (Posts/Day, Hours)').first().json.endHour);
const lastScheduledTime = new
Date($input.first().json.lastScheduledTime);
// Get video data
const videos = $('List Generated Clip Ideas').all();
const finalShorts = $('Fetch Final Shorts URLs').all();
const now = new Date();
const totalHours = endHour - startHour;
const intervalMs = (totalHours * 60 * 60 * 1000) / postsPerDay;
// Start from the day after the last scheduled post
let startDate = new Date(lastScheduledTime);
startDate.setDate(startDate.getDate() + 1);
startDate.setHours(startHour, 0, 0, 0);
// If starting date is in the past, use tomorrow
if (startDate < now) {
startDate = new Date();
startDate.setDate(startDate.getDate() + 1);
startDate.setHours(startHour, 0, 0, 0);
}
const result = [];
let videoIndex = 0;
let currentDate = new Date(startDate);
let dailyPostCount = 0;
while (videoIndex < videos.length) {
// Calculate time slot
const baseTime = new Date(currentDate);
baseTime.setHours(startHour + (dailyPostCount * (totalHours /
postsPerDay)), 0, 0, 0);
// Add randomness ±30 min
const offset = (Math.random() - 0.5) * 60 * 60 * 1000;
const scheduledTime = new Date(baseTime.getTime() + offset);
// Get video data
const video = videos[videoIndex].json;
const finalShort = finalShorts[videoIndex].json;
result.push({
json: {
scheduled_at: scheduledTime.toISOString(),
video_name: video.name,
video_virality_score: video.virality_score,
video_caption: video.publication_captions.youtube,
video_tiktok_caption: video.publication_captions.tiktok,
video_url: finalShort.src_url,
video_id: video.id
}
});
videoIndex++;
dailyPostCount++;
// Move to next day if daily limit reached
if (dailyPostCount >= postsPerDay) {
currentDate.setDate(currentDate.getDate() + 1);
dailyPostCount = 0;
}
}
return result;

return $input.all().map(item => {
const utc = item.json.scheduled_at;
const local = new Date(utc).toLocaleString("en-US", {
timeZone: "Europe/Paris",
timeZoneName: "short",
hour12: false
});
return {
json: {
...item.json, // This includes all the video data
local_time: local
}
};
});

