const output = [];

$input.all().forEach(item => {
  const persons = ((item.json.results || {}).persons) || [];
  persons.forEach(person => {
    // Owner info
    const owner_name = `${person.name.first || ""} ${person.name.middle || ""} ${person.name.last || ""}`.trim();
    const property = person.property || {};
    const propAddress = person.propertyAddress || {};
    const mailing = person.mailingAddress || {};

    // Property meta
    const property_id = property.id || "";
    const address = propAddress.street || "";
    const city = propAddress.city || "";
    const state = propAddress.state || "";
    const zip = propAddress.zip || "";

    // Property info
    const absentee_owner = (property.absenteeOwner === true) ? "Yes" : "No";
    const equity = property.equity || "";
    const equity_percent = property.equityPercent || "";
    const owner_mailing_address = mailing.street || "";
    const owner_mailing_city = mailing.city || "";
    const owner_mailing_state = mailing.state || "";
    const owner_mailing_zip = mailing.zip || "";

    // Create row object with basic info
    const row = {
      owner_name,
      property_id,
      address,
      city,
      state,
      zip,
      absentee_owner,
      equity,
      equity_percent,
      owner_mailing_address,
      owner_mailing_city,
      owner_mailing_state,
      owner_mailing_zip,
      lead_source: 'BatchData Property Search',
      date_added: new Date().toISOString().split('T')[0]
    };

    // Add phones as phone_1, phone_2, etc.
    (person.phoneNumbers || []).forEach((phone, idx) => {
      const n = idx + 1;
      row[`phone_${n}`] = phone.number;
      row[`phone_${n}_type`] = phone.type;
      row[`phone_${n}_carrier`] = phone.carrier;
      row[`phone_${n}_tested`] = phone.tested ? "Yes" : "No";
      row[`phone_${n}_reachable`] = phone.reachable ? "Yes" : "No";
      row[`phone_${n}_dnc`] = phone.dnc ? "Yes" : "No";
      row[`phone_${n}_last_reported`] = phone.lastReportedDate;
      row[`phone_${n}_score`] = phone.score;
    });

    // Add emails as email_1, email_2, etc.
    (person.emails || []).forEach((email, idx) => {
      const n = idx + 1;
      row[`email_${n}`] = email.address || email;
    });

    output.push(row);
  });
});

return output.map(row => ({ json: row }));

// Summarize the results of the property lead search
const leads = $input.all();
const totalLeads = leads.length;

// Calculate the highest lead score
let highestScore = 0;
if (totalLeads > 0) {
  highestScore = Math.max(...leads.map(item => item.json.lead_score || 0));
}

// Return a summary object
return {
  json: {
    total_leads: totalLeads,
    highest_score: highestScore,
    execution_date: new Date().toISOString(),
    success: true
  }
};
