// Merge subject + snippet, normalize, strip invisibles, lowercase
const subject = String($json.Subject ?? "");
const snippet = String($json.snippet ?? "");

let text = `${subject} ${snippet}`
  .normalize('NFKC')
  // strip zero-width / soft hyphens / CGJ that show up in Gmail previews
  .replace(/[\u200B-\u200D\uFEFF\u2060\u00AD\u034F]/g, '')
  .toLowerCase();

// keywords to match (word boundaries to avoid false positives)
const keywords = [
  "service","services","price","cost","quote","automation","network",
  "help","question","inquiry","business","hours","contact","consulting","support"
];
const pattern = new RegExp(`\\b(${keywords.join('|')})\\b`, 'i');
const should_process = pattern.test(text);

return [{
  json: {
    ...$json,
    prefilter_text: text,
    should_process
  }
}];

// Token Usage and Cost Tracking for Gemini 2.5 Flash
// Enhanced version that also preserves original email data

const input = $input.first().json;

// Get the actual output text from the AI agent
const outputText = input.output || '';

// Get original email data from the Pre-Filter node
const originalEmailData = $('Pre-Filter Business Emails').first().json;

// Estimate input tokens (system message + email content + prompt)
const systemMessageLength = 800; // Approximate length of your system message
const emailContentLength = (originalEmailData.snippet || '').length;
const promptTemplateLength = 200; // Your prompt template
const estimatedInputChars = systemMessageLength + emailContentLength + promptTemplateLength;
const estimatedInputTokens = Math.ceil(estimatedInputChars / 4);

// Estimate output tokens based on actual response
const estimatedOutputTokens = Math.ceil(outputText.length / 4);
const estimatedTotalTokens = estimatedInputTokens + estimatedOutputTokens;

// Gemini 2.5 Flash pricing
const inputPrice = 0.075;  // per 1M tokens
const outputPrice = 0.30;  // per 1M tokens

// Calculate costs
const inputCost = (estimatedInputTokens / 1000000) * inputPrice;
const outputCost = (estimatedOutputTokens / 1000000) * outputPrice;
const totalCost = inputCost + outputCost;

// Create enhanced output with token tracking AND original email data
const enhancedOutput = {
  // Preserve AI agent output
  ...input,

  // Add original email data for logging
  originalEmail: {
    id: originalEmailData.id,
    threadId: originalEmailData.threadId,
    From: originalEmailData.From,
    Subject: originalEmailData.Subject,
    snippet: originalEmailData.snippet
  },

  // Enhanced token usage
  tokenUsage: {
    inputTokens: estimatedInputTokens,
    outputTokens: estimatedOutputTokens,
    totalTokens: estimatedTotalTokens,
    inputCost: parseFloat(inputCost.toFixed(6)),
    outputCost: parseFloat(outputCost.toFixed(6)),
    totalCost: parseFloat(totalCost.toFixed(6)),
    costBreakdown: {
      inputCostPer1M: inputPrice,
      outputCostPer1M: outputPrice,
      calculatedAt: new Date().toISOString(),
      dataSource: 'text_estimation',
      estimationNote: 'Estimated at ~4 chars per token'
    }
  },

  // Helper fields for logging
  hasResponse: Boolean(outputText && outputText.trim().length > 0),
  processedAt: new Date().toISOString()
};

// Log the estimation
console.log('=== TOKEN TRACKING WITH EMAIL DATA ===');
console.log('Email Subject:', originalEmailData.Subject);
console.log('Email From:', originalEmailData.From);
console.log('AI Output Length:', outputText.length);
console.log('Token Usage:', {
  inputTokens: estimatedInputTokens,
  outputTokens: estimatedOutputTokens,
  totalTokens: estimatedTotalTokens,
  totalCostUSD: totalCost.toFixed(6)
});
console.log('Has Response:', enhancedOutput.hasResponse);

return enhancedOutput;

