  .replace(/\n\n/g, '</p><p>')            // paragraph breaks
  .replace(/\n/g, '<br/>')                // single newlines
  .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')  // bold

// Bullets: lines that start with "* "
html = html.replace(/<br\/>\*\s+/g, '<br/><li>');
html = html.replace(/<\/p><p>\*\s+/g, '</p><ul><li>');

// Close <ul> if opened
if (html.includes('<ul>') && !html.includes('</ul>')) {
  html += '</ul>';
}

